mapper.select:
select rno, bno, reply, replyer, replydate, updatedate, exist
from tbl_reply
where bno = #{bno}
order by rno asc
limit #{cri.startIndex}, #{cri.amount}

select rr.*
from tbl_r_reply rr, (
	select rno
	from tbl_reply
	where bno = #{bno}
	limit #{cri.startIndex}, #{cri.amount}
) r
where rr.rno = r.rno;
